// RigidShape.js
import Vec2 from './Vec2';

class RigidShape {
  constructor(center) {
    this.mCenter = center;
    this.mAngle = 0;
    this.mFix = 0;
    this.mFaceNormal = [];
    this.update = this.update.bind(this);
  }

  update() {
    if (this.mCenter.y < gEngine.Core.mHeight && this.mFix !== 0) {
      this.move(new Vec2(0, 1));
    }
    this.rotate(0.1);
  }

  move(vec) {
    this.mCenter = this.mCenter.add(vec);
  }

  rotate(angle) {
    this.mAngle += angle;
  }
}

export default RigidShape;
