import { useState } from 'react';
import Vec2 from './Vec2'; // Assuming Vec2 is imported

function useCollisionInfo() {
  const [collisionInfo, setCollisionInfo] = useState({
    mDepth: 0,
    mNormal: new Vec2(0, 0),
    mStart: new Vec2(0, 0),
    mEnd: new Vec2(0, 0),
  });

  const setNormal = (normal) => {
    setCollisionInfo((prev) => ({ ...prev, mNormal: normal }));
  };

  const getDepth = () => collisionInfo.mDepth;

  const getNormal = () => collisionInfo.mNormal;

  const setInfo = (depth, normal, start) => {
    setCollisionInfo((prev) => ({
      ...prev,
      mDepth: depth,
      mNormal: normal,
      mStart: start,
      mEnd: start.add(normal.scale(depth)),
    }));
  };

  const changeDir = () => {
    setCollisionInfo((prev) => {
      const newNormal = prev.mNormal.scale(-1);
      return {
        ...prev,
        mNormal: newNormal,
        mStart: prev.mEnd,
        mEnd: prev.mStart,
      };
    });
  };

  return { setNormal, getDepth, getNormal, setInfo, changeDir };
}

export default useCollisionInfo;
