    }

    // Reset with the R key
    if (keycode === 82) { // R
      allObjectsRef.current.splice(1, allObjects.length - 1); // Keep only the first object
      gObjectNumRef.current = 0;
    }

  //   if (window.event) { // IE    
  //     keycode = event.keyCode;
